import axios from "axios";
const API_CHAT = "http://localhost:8022/api/chats";
let user = JSON.parse(localStorage.getItem('user'));
class ChatService {

    getAllChats(){
       return axios.get(API_CHAT + "/").then(response => response.data);
    }

}



export default new ChatService();
